    function insertIcons(localCurrentCard, render) {
        if (!render) return;
        if (C_LOGGING) console.log("MAXSM-RATINGS", "card: " + localCurrentCard + ", Insert icons");
    
        var showIcons = localStorage.getItem('maxsm_ratings_icons') === 'true';
    
        function replaceIcon(className, svg) {
            var Element = $('.' + className, render);
            if (!Element.length) return;
    
            // 丕亟舒仍�亠仄 ��舒��亠 从仂仆�亠亶仆亠��
            Element.find('.maxsm-icon-container').remove();
    
            // �仂���舒仆舒于仍亳于舒亠仄 �从����亠 �仍亠仄亠仆��
            Element.find('.maxsm-hide').removeClass('maxsm-hide');
            
            var target;
            
            if (showIcons) {
                // �仂亳�从 �亠仍亠于仂亞仂 �仍亠仄亠仆�舒 亟仍� 亰舒仄亠仆� (仆舒亰于舒仆亳亠 亳��仂�仆亳从舒)
                target = Element.find('.source--name');
                var isSourceName = true;
                
                if (!target.length) {
                    var childDivs = Element.children('div');
                    if (childDivs.length >= 2) {
                        target = childDivs.eq(0); // �亠�亠仄 ����乘� div (仆舒亰于舒仆亳亠)
                        isSourceName = false;
                    }
                }
    
                if (target.length) {
                    // 弌仂亰亟舒�仄 从仂仆�亠亶仆亠� 亟仍� 亳从仂仆从亳
                    var iconWrap = $('<div></div>');
                    iconWrap.addClass('maxsm-icon-container');
                    iconWrap.html(svg);
                    
                    // 弌仂��舒仆�亠仄 仂�亳亞亳仆舒仍�仆�亶 HTML 仗亠�亠亟 亰舒仄亠仆仂亶
                    if (!target.data('original-html')) {
                        target.data('original-html', target.html());
                    }
                    
                    // �舒仄亠仆�亠仄 �仂亟亠�亢亳仄仂亠 �亠仍亠于仂亞仂 �仍亠仄亠仆�舒 仆舒 亳从仂仆从�
                    target.html(iconWrap);
                    
                    // �仂弍舒于仍�亠仄 从仍舒�� 亟仍� ��亳仍亳亰舒�亳亳
                    target.addClass('rate--icon');
                }
            } else {
                // �仂���舒仆舒于仍亳于舒亠仄 仂�亳亞亳仆舒仍�仆�亶 HTML
                target = Element.find('.rate--icon');
                if (target.length && target.data('original-html')) {
                    target.html(target.data('original-html'));
                    target.removeClass('rate--icon');
                    // ��亳�舒亠仄 �仂��舒仆亠仆仆�亠 亟舒仆仆�亠
                    target.removeData('original-html');
                }
            }
        }
    
        // ��亳仄亠仆�亠仄 从仂 于�亠仄 亳��仂�仆亳从舒仄
        replaceIcon('rate--imdb', imdb_svg);
        replaceIcon('rate--kp', kp_svg);
        replaceIcon('rate--tmdb', tmdb_svg);
        replaceIcon('rate--oscars', oscars_svg);
        replaceIcon('rate--emmy', emmy_svg);
        replaceIcon('rate--awards', awards_svg);
        replaceIcon('rate--rt', rt_svg);
        replaceIcon('rate--mc', mc_svg);
        replaceIcon('rate--avg', avg_svg);
    }
    
